<!-- STYLES ///////////////////////////////////////////////////// -->
<style>
  section {
    height: var(--viewport-height);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }
  h3 {
    width: 100%;
    font-family: "Montserrat", sans-serif;
    color: var(--color2);
    font-size: 1.5rem;
    text-align: center;
    padding: 1rem 0;
    background-color: var(--color1);
    margin-bottom: 0.5rem;
  }
  ul {
    flex: 1;
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    justify-content: center;
    row-gap: 0.1rem;
    column-gap: 0.1rem;
    align-content: center;
  }
  li {
    position: relative;
    background-color: var(--color1-1);
    width: 49%;
    color: none;
    height: 28vw;
    max-height: 20vh;
  }
  li:nth-of-type(1) {
    color: #DD4B25;
  }
  li:nth-of-type(2) {
    color: cyan;
  }
  li:nth-of-type(3) {
    color: #EDD718;
  }
  li:nth-of-type(4) {
    color: #FFB13B;
  }
  li:nth-of-type(5) {
    color: #00D1F7;
  }
  li:nth-of-type(6) {
    color: #FF3E00
  }
  li:nth-of-type(7) {
    color: #8AC640;
  }
  h4 {
    padding: 0.5rem;
    font-size: 1.2rem;
  }
  svg, img {
    position: absolute;
    right: 0;
    width: 50%;
    height: 80%;
    bottom: 0%;
    padding: 0.3rem;
  }
  li:nth-of-type(3) svg {
    height: 75%;
    top: 25%;
  }
  a {
    font-size: 1.5rem;
    padding: 0.8rem 0.5rem;
    margin: 2vh 0;
    border-radius: 0.2rem;
  }

  @media screen and (min-width: 600px) {
    h4 {
      font-size: 1.8rem;
    }
  }

  @media screen and (orientation: landscape) {
    h3 {
      font-size: 2.5rem;
    }
    li {
      display: flex;
      justify-content: center;
      align-items: center;
      width: 30%;
      height: 20vh;
      column-gap: 1rem;
    }
    svg, img {
      height: 70%;
      width: auto;
      position: static;
    }
  }
</style>